import './App.css';
import Article from "./article/Article"
import articles from "./.data/db.json"
import {useState} from 'react';

function App() {

  const [articlesList, setArticleList] = useState(articles)

  return (
    <div className="App">
      <button>Add</button>
      {
        articlesList.map ((article) => 
        <Article {...article} />
        )
      }
      </div>
  );
}

export default App;
